import React, { useState } from "react";
import { Container, Card, Row, Col, Button } from "react-bootstrap";
import jersey from "./IMG/jersey.jpg";
import necklace from "./IMG/necklace.jpg";
import watch from "./IMG/watch.jpg";
import bag from "./IMG/bag.jpg";
import airpod from "./IMG/airpod.jpg";
import purse from "./IMG/purse.jpg";
import suit from "./IMG/suit.jpg";
import slippers from "./IMG/slippers.jpg";

function Products() {
  const [cart, setCart] = useState([]);
  const [page, setPage] = useState("products");

  const products = [
    {
      title: "Necklace",
      cost: "$5.99",
      image: necklace,
    },
    {
      title: "Jersey",
      cost: "$5.99",
      image: jersey,
    },
    {
      title: "Watch",
      cost: "$5.99",
      image: watch,
    },
    {
      title: "Bag",
      cost: "$5.99",
      image: bag,
    },
    {
      title: "Airpod",
      cost: "$5.99",
      image: airpod,
    },
    {
      title: "Purse",
      cost: "$5.99",
      image: purse,
    },
    {
      title: "Suit",
      cost: "$5.99",
      image: suit,
    },
    {
      title: "Slippers",
      cost: "$5.99",
      image: slippers,
    },
  ];

  const addToCart = (product) => {
    console.log("added to cart");
    setCart([...cart, product]);
  };

  return (
    <div className=" bg-primary py-2">
      <Container>
        <header>
          <Button>Go to Cart ({cart.length})</Button>
        </header>
        {page === "products" && (
          <>
            <h2 className="text-light">FEATURED PRODUCTS</h2>
            <Row className="g-2">
              {products.map((product) => {
                return (
                  <Col xs={6} md={4} xl={3} key={product.title}>
                    <Card>
                      <Card.Img
                        style={{ height: "220px" }}
                        src={product.image}
                        alt="show image"
                      />
                      <Card.Text>{product.cost}</Card.Text>
                      <Card.Title>{product.title}</Card.Title>
                      <Button
                        onClick={() => {
                          addToCart(product);
                        }}
                      >
                        Add To Cart
                      </Button>
                    </Card>
                  </Col>
                );
              })}
            </Row>
          </>
        )}
      </Container>
    </div>
  );
}

export default Products;
