import React from "react";
import room from "./IMG/room.jpg";
import poolside from "./IMG/poolside.jpg";
import kitchen from "./IMG/kitchen.jpg";
import { Carousel } from "react-bootstrap";
// import { Navbar } from "react-bootstrap";
// import { Container } from "react-bootstrap";
// import { Nav } from "react-bootstrap";
// import { Form } from "react-bootstrap";
// import { Button } from "react-bootstrap";
// import "./Site.css";
import "./Styles.css";
import { AiOutlineShoppingCart } from "react-icons/ai";
import { Link } from "react-router-dom";

function Navbars() {
  return (
    <div>
      <div className="navbar">
        <h2>React-Website</h2>
        <ul className="links">
          <li>
            <Link className="link" to="/">
              Home
            </Link>
          </li>
          <li>
            <Link className="link" to="/product">
              Product
            </Link>
          </li>
          <li>
            <Link className="link" to="/blog">
              Blog
            </Link>
          </li>
          <li>
            <Link className="link" to="/contact">
              Contact
            </Link>
          </li>
          <li>
            <Link className="link" to="/cart">
              <AiOutlineShoppingCart />
            </Link>
          </li>
        </ul>
        <form>
          <input className="search" type="search" placeholder="Enter search" />
          <button className="submit" type="submit">
            Send
          </button>
        </form>
      </div>
      <Carousel>
        <Carousel.Item interval={1000}>
          <img className="d-block w-100" src={poolside} alt="First slide" />
          <Carousel.Caption>
            <h3>POOLSIDE</h3>
            <p className="fw-bold">
              Nulla vitae elit libero, a pharetra augue mollis interdum.
            </p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item interval={1000}>
          <img className="d-block w-100" src={room} alt="Second slide" />
          <Carousel.Caption>
            <h3>ROOM</h3>
            <p className="fw-bold">
              Lorem ipsum dolor sit amet, consectetur adipiscing elit.
            </p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img className="d-block w-100" src={kitchen} alt="Third slide" />
          <Carousel.Caption>
            <h3>KITCHEN</h3>
            <p className="fw-bold">
              Praesent commodo cursus magna, vel scelerisque nisl consectetur.
            </p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>
      <div className=" bg-primary py-2">
        <Container>
          <h2 className="text-light">FEATURED PRODUCTS</h2>
          <Row className="g-2">
            {products.map((product, idx) => {
              return (
                <Col xs={6} md={4} xl={3} key={product.title}>
                  <Card>
                    <Card.Img
                      style={{ height: "220px" }}
                      src={product.image}
                      alt="show image"
                    />
                    <Card.Text>{product.cost}</Card.Text>
                    <Card.Title>{product.title}</Card.Title>
                    <Button>Add To Cart</Button>
                  </Card>
                </Col>
              );
            })}
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Navbars;

