import React from "react";
import { Container, Card, Row, Col, Button } from "react-bootstrap";
import jersey from "./IMG/jersey.jpg";
import necklace from "./IMG/necklace.jpg";
import watch from "./IMG/watch.jpg";
import bag from "./IMG/bag.jpg";
import airpod from "./IMG/airpod.jpg";
import purse from "./IMG/purse.jpg";
import suit from "./IMG/suit.jpg";
import slippers from "./IMG/slippers.jpg";

function Category2() {
  const products = [
    {
      title: "Necklace",
      cost: "$5.99",
      image: necklace,
    },
    {
      title: "Jersey",
      cost: "$5.99",
      image: jersey,
    },
    {
      title: "Watch",
      cost: "$5.99",
      image: watch,
    },
    {
      title: "Bag",
      cost: "$5.99",
      image: bag,
    },
    {
      title: "Airpod",
      cost: "$5.99",
      image: airpod,
    },
    {
      title: "Purse",
      cost: "$5.99",
      image: purse,
    },
    {
      title: "Suit",
      cost: "$5.99",
      image: suit,
    },
    {
      title: "Slippers",
      cost: "$5.99",
      image: slippers,
    },
  ];
  const products1 = [
    {
      title: "Bracelet",
      cost: "$6.99",
      image: bracelet,
    },
    {
      title: "Drone",
      cost: "$6.99",
      image: drone,
    },
    {
      title: "Flower",
      cost: "$6.99",
      image: flower,
    },
    {
      title: "Laptop",
      cost: "$6.99",
      image: laptop,
    },
    {
      title: "Mouse",
      cost: "$6.99",
      image: mouse,
    },
    {
      title: "Ring",
      cost: "$6.99",
      image: ring,
    },
    {
      title: "Seat",
      cost: "$6.99",
      image: seat,
    },
    {
      title: "Hoodie",
      cost: "$6.99",
      image: hoodie,
    },
  ];
  return (
    <div>
      <div className=" bg-primary py-2">
        <Container>
          <h2 className="text-light">FEATURED PRODUCTS</h2>
          <Row className="g-2">
            {products.map((product, idx) => {
              return (
                <Col xs={6} md={4} xl={3} key={product.title}>
                  <Card>
                    <Card.Img
                      style={{ height: "220px" }}
                      src={product.image}
                      alt="show image"
                    />
                    <Card.Text>{product.cost}</Card.Text>
                    <Card.Title>{product.title}</Card.Title>
                    <Button>Add To Cart</Button>
                  </Card>
                </Col>
              );
            })}
          </Row>
        </Container>
      </div>
      <div className="bg-dark py-3">
        <Container>
          <h2 className="text-light">MORE TO LOVE</h2>
          <Row className="g-2">
            {products1.map((product) => {
              return (
                <Col xs={6} md={4} xl={3}>
                  <Card>
                    <Card.Img
                      style={{ height: "220px" }}
                      src={product.image}
                      alt
                      rounded
                    />
                    <Card.Text>{product.cost}</Card.Text>
                    <Card.Title>{product.title}</Card.Title>
                    <Button>Add To Cart</Button>
                  </Card>
                </Col>
              );
            })}
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Category2;
