import React from "react";
import { Container, Card, Row, Col, Button } from "react-bootstrap";
import jersey from "./IMG/jersey.jpg";
import necklace from "./IMG/necklace.jpg";
import watch from "./IMG/watch.jpg";
import bag from "./IMG/bag.jpg";
import airpod from "./IMG/airpod.jpg";
import purse from "./IMG/purse.jpg";
import suit from "./IMG/suit.jpg";
import slippers from "./IMG/slippers.jpg";
import bracelet from "./IMG1/bracelet.jpg";
import drone from "./IMG1/drone.jpg";
import flower from "./IMG1/flower.jpg";
import laptop from "./IMG1/laptop.jpg";
import mouse from "./IMG1/mouse.jpg";
import ring from "./IMG1/ring.jpg";
import seat from "./IMG1/seat.jpg";
import hoodie from "./IMG1/hoodie.jpg";
import room from "./IMG/room.jpg";
import poolside from "./IMG/poolside.jpg";
import kitchen from "./IMG/kitchen.jpg";
import { Carousel } from "react-bootstrap";
import "./Styles.css";

function Category2() {
  const products = [
    {
      title: "Necklace",
      cost: "$5.99",
      image: necklace,
    },
    {
      title: "Jersey",
      cost: "$5.99",
      image: jersey,
    },
    {
      title: "Watch",
      cost: "$5.99",
      image: watch,
    },
    {
      title: "Bag",
      cost: "$5.99",
      image: bag,
    },
    {
      title: "Airpod",
      cost: "$5.99",
      image: airpod,
    },
    {
      title: "Purse",
      cost: "$5.99",
      image: purse,
    },
    {
      title: "Suit",
      cost: "$5.99",
      image: suit,
    },
    {
      title: "Slippers",
      cost: "$5.99",
      image: slippers,
    },
  ];
  const products1 = [
    {
      title: "Bracelet",
      cost: "$6.99",
      image: bracelet,
    },
    {
      title: "Drone",
      cost: "$6.99",
      image: drone,
    },
    {
      title: "Flower",
      cost: "$6.99",
      image: flower,
    },
    {
      title: "Laptop",
      cost: "$6.99",
      image: laptop,
    },
    {
      title: "Mouse",
      cost: "$6.99",
      image: mouse,
    },
    {
      title: "Ring",
      cost: "$6.99",
      image: ring,
    },
    {
      title: "Seat",
      cost: "$6.99",
      image: seat,
    },
    {
      title: "Hoodie",
      cost: "$6.99",
      image: hoodie,
    },
  ];
  return (
    <div>
      <div className="categories bg-primary py-2">
        <Container>
          <h2 className="text-light">FEATURED PRODUCTS</h2>
          <Row className="g-2">
            {products.map((product, idx) => {
              return (
                <Col xs={6} md={4} xl={3} key={product.title}>
                  <Card>
                    <Card.Img
                      style={{ height: "220px" }}
                      src={product.image}
                      alt="show image"
                    />
                    <Card.Text>{product.cost}</Card.Text>
                    <Card.Title>{product.title}</Card.Title>
                    <Button>Add To Cart</Button>
                  </Card>
                </Col>
              );
            })}
          </Row>
        </Container>
      </div>
      <div className="bg-dark py-3">
        <Container>
          <h2 className="text-light">MORE TO LOVE</h2>
          <Row className="g-2">
            {products1.map((product) => {
              return (
                <Col xs={6} md={4} xl={3}>
                  <Card>
                    <Card.Img
                      style={{ height: "220px" }}
                      src={product.image}
                      alt
                      rounded
                    />
                    <Card.Text>{product.cost}</Card.Text>
                    <Card.Title>{product.title}</Card.Title>
                    <Button>Add To Cart</Button>
                  </Card>
                </Col>
              );
            })}
          </Row>
        </Container>
      </div>
      <Carousel>
        <Carousel.Item interval={0}>
          <img className="d-block w-100" src={poolside} alt="First slide" />
          <Carousel.Caption>
            <h3>POOLSIDE</h3>
            <p className="fw-bold">
              Nulla vitae elit libero, a pharetra augue mollis interdum.
            </p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item interval={1000}>
          <img className="d-block w-100" src={room} alt="Second slide" />
          <Carousel.Caption>
            <h3>ROOM</h3>
            <p className="fw-bold">
              Lorem ipsum dolor sit amet, consectetur adipiscing elit.
            </p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img className="d-block w-100" src={kitchen} alt="Third slide" />
          <Carousel.Caption>
            <h3>KITCHEN</h3>
            <p className="fw-bold">
              Praesent commodo cursus magna, vel scelerisque nisl consectetur.
            </p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>
    </div>
  );
}

export default Category2;
