import React from "react";
import "./Styles.css";
import { Row, Col, Card, Container, Button } from "react-bootstrap";

function Cart({ cartItems, handleAddProduct1 }) {
  return (
    <div className="cart-items">
      <div className="cart-items-header">Cart Items</div>

      {cartItems.length === 0 && (
        <div className="cart-items-empty">No items are added</div>
      )}
      <Container>
        <Row>
          {cartItems.map((item) => (
            <Col xs={6} md={4} xl={3} key={item.id} className="cart-items-list">
              <Card>
                <Card.Img
                  style={{ height: "220px" }}
                  className="cart-items-image"
                  src={item.image}
                  alt={item.name}
                />
                <Card.Text style={{ color: "black" }}>{item.title}</Card.Text>
                <div className="btns">
                  <Button
                    className="cart-item-add"
                    onClick={() => handleAddProduct1}
                  >
                    +
                  </Button>
                  <Button className="cart-item-remove">-</Button>
                </div>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
}

export default Cart;
